import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image } from 'react-native';
import { StackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';

import KakaoLogin from './KakaoLogin';

const Stack = StackNavigator();

export default function Login() {
  const [selectedLogin, setSelectedLogin] = useState('');

  // 누르는 버튼에 따라 어떤 방식으로 로그인 하는지 구분
  const handleLoginSelection = (loginType) => {
    setSelectedLogin(loginType);
    navigation.navigate('KakaoLogin');
  };

  return (
    <View style={styles.container}>

      <Text> 위에는 로고를 넣을겁니다. </Text>

      <TouchableOpacity style={styles.naverLoginButton} onPress={() => handleLoginSelection('naver')}>
        <Image source={require('../image/naver.png')} style={styles.loginButtonImage} />
      </TouchableOpacity>
      <TouchableOpacity style={styles.kakaoLoginButton} onPress={() => handleLoginSelection('kakao')}>
        <Image source={require('../image/kakao.png')} style={styles.loginButtonImage} />
      </TouchableOpacity>
      <TouchableOpacity style={styles.googleLoginButton} onPress={() => handleLoginSelection('google')}>
        <Image source={require('../image/google.png')} style={styles.loginButtonImage} />
      </TouchableOpacity>

    </View>
  );
}

const HomeScreen = ({ navigation }) => {

}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  naverLoginButton: { // 버튼은 클릭 시 이벤트 및 이미지 액자
    marginBottom: 12,
  },
  kakaoLoginButton: {
    marginBottom: 12,
  },
  googleLoginButton: {
    marginBottom: 12,
  },
  loginButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  loginButtonImage: {
    borderRadius: 8, // 둥근 효과
    width: 170, // 이미지 너비 조절
    height: 40, // 이미지 높이 조절
  }
});
