import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image, Modal } from 'react-native';
import KaKaoLogin from './KakaoLogin';

export default function Login() {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [selectedLogin, setSelectedLogin] = useState('');

  // 누르는 버튼에 따라 어떤 방식으로 로그인 하는지 구분
  const handleLoginSelection = (loginType) => {
    setIsModalVisible(true);
    setSelectedLogin(loginType);
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  return (
    <View style={styles.container}>

      <Text> 위에는 로고를 넣을겁니다. </Text>

      <TouchableOpacity style={styles.naverLoginButton} onPress={() => handleLoginSelection('naver')}>
        <Image source={require('../image/naver.png')} style={styles.loginButtonImage} resizeMode="cover" />
      </TouchableOpacity>
      <TouchableOpacity style={styles.kakaoLoginButton} onPress={() => handleLoginSelection('kakao')}>
        <Image source={require('../image/kakao.png')} style={styles.loginButtonImage} resizeMode="cover" />
      </TouchableOpacity>
      <TouchableOpacity style={styles.googleLoginButton} onPress={() => handleLoginSelection('google')}>
        <Image source={require('../image/google.png')} style={styles.loginButtonImage} resizeMode="cover" />
      </TouchableOpacity>

      <Modal visible={isModalVisible} animationType="slide">
        {/* 로그인 모달 내용 */}
        <KakaoLogin onClose={closeModal} />
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  naverLoginButton: { // 버튼은 클릭 시 이벤트 및 이미지 액자
    marginBottom: 12,
  },
  kakaoLoginButton: {
    marginBottom: 12,
  },
  googleLoginButton: {
    marginBottom: 12,
  },
  loginButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  loginButtonImage: {
    borderRadius: 8, // 둥근 효과
    width: 170, // 이미지 너비 조절
    height: 40, // 이미지 높이 조절
  },
  modalContent: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
});
