import React from 'react';
import { View, StyleSheet, TouchableOpacity, Text } from 'react-native';
import { AuthSession } from 'expo-auth-session';
import { WebView } from 'react-native-webview';

const NV_CLIENT_ID = 'YOUR_NAVER_CLIENT_ID'; // 네이버 클라이언트 아이디
const NV_STATE_STRING = 'YOUR_STATE_STRING'; // 상태 문자열 설정

export default function NaverLogin() {
  const [showLogin, setShowLogin] = React.useState(false);

  const handlePressAsync = async () => {
    setShowLogin(true);
  };

  const handleNavigationStateChange = async (newState) => {
    if (newState.url.includes(NV_REDIRECT_URI)) {
      setShowLogin(false);
      const responseUrl = newState.url;
      // 여기서 responseUrl에서 필요한 정보를 추출하고 처리합니다.
    }
  };

  return (
    <View style={styles.container}>

        <WebView
          source={{
            uri: `https://nid.naver.com/oauth2.0/authorize?response_type=code&client_id=${NV_CLIENT_ID}&redirect_uri=${NV_REDIRECT_URI}&state=${NV_STATE_STRING}`,
          }}
          onNavigationStateChange={handleNavigationStateChange}
          style={styles.webview}
        />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  webview: {
    flex: 1,
    width: '100%',
  },
});
