import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Modal } from 'react-native';

export default function Login() {
  const Login = "";
  const [isModalVisible, setIsModalVisible] = useState(false, Login);

  // 구글 로그인 모달
  const handleGoogleLogin = () => {
    setIsModalVisible(true);
    Login = "google";
  };

  // 카카오
  const handleKakaoLogin = () => {
    setIsModalVisible(true);
    Login = "kakao";
  };

  // 네이버
  const handleNaverLogin = () => {
    setIsModalVisible(true);
    Login = "naver";
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.loginButton} onPress={handleGoogleLogin}>
        <Text style={styles.loginButtonText}>구글 로그인</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.loginButton} onPress={handleKakaoLogin}>
        <Text style={styles.loginButtonText}>카카오 로그인</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.loginButton} onPress={handleNaverLogin}>
        <Text style={styles.loginButtonText}>네이버 로그인</Text>
      </TouchableOpacity>

      <Modal visible={isModalVisible, Login} animationType="slide">
        {/* 로그인 모달 내용 */}
        <View style={styles.modalContent}>
          {/* 로그인 모달 내용 구현 */}
          <Text>로그인 모달 내용</Text>
          <TouchableOpacity onPress={closeModal}>
            <Text>닫기</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  loginButton: {
    backgroundColor: '#4285F4',
    paddingVertical: 12,
    paddingHorizontal: 24,
    borderRadius: 8,
    marginBottom: 12,
  },
  loginButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  modalContent: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
});
